import Axios from "axios";
import React, { useState } from "react";
import FileDownload from 'js-file-download'
import "./App.css";

function App() {
  const [customers, setCustomers] = useState();
  const [products, setProducts] = useState();
  const [orders, setOrders] = useState();

  const load = (event) => {
    const data = new FormData();
    data.append('files', customers);
    data.append('files', products);
    data.append('files', orders);

    Axios.post("http://localhost:3001/upload",data)
      .then(res=>console.log(res))
      .catch(err => console.log(err));
  };
const download = (type) => {
    Axios.get(`http://localhost:3001/reports/${type}`, {
      responseType: 'blob',
    }).then(response => {
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${type}.csv`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);
    });
  }


  return (
    <div className="App">
      <header className="App-header">
        <form action="#">
          <div className="flex">
            <label htmlFor="customers">Customers</label>
            <input type="file" id="customers" accept=".csv"
              onChange={(event) => {
                setCustomers(event.target.files[0]);
              }}/>
          </div>
          <div className="flex">
            <label htmlFor="products">Products</label>
            <input
              type="file"
              id="products"
              accept=".csv"
              onChange={(event) => {
                setProducts(event.target.files[0]);
              }}
            />
          </div>
          <div className="flex">
            <label htmlFor="orders">Orders</label>
            <input type="file" id="orders" accept=".csv"
              onChange={(event) => {
                setOrders(event.target.files[0]);
              }}/>
          </div>
        </form>
        <button onClick={load}>Load</button>
        <button onClick={() => download('order_prices')}>Reporte 1</button>
        <button onClick={() => download('product_customers')}>Reporte 2</button>
        <button onClick={() => download('customer_ranking')}>Reporte 3</button>
      </header>
    </div>
  );
}
export default App;
