import Axios from "axios";
import React, { useState } from "react";

import "./App.css";

function App() {
  const [customers, setCustomers] = useState();
  const [products, setProducts] = useState();
  const [orders, setOrders] = useState();

  const load = (event) => {
    const data = new FormData();
    data.append('files', customers);
    data.append('files', products);
    data.append('files', orders);

    Axios.post("http://localhost:3001/upload",data)
      .then(res=>console.log(res))
      .catch(err => console.log(err));
  };

  const orderPrices = (event) => {
    Axios.get('http://localhost:3001/reports/order-prices', {
      responseType: 'blob',
    }).then(response => {
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'order_prices.csv');
      document.body.appendChild(link);
      link.click();
    });

  };
  const productCustomers = (event) => {
    Axios.get('http://localhost:3001/reports/product-customers', {
      responseType: 'blob',
    }).then(response => {
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'product_customers.csv');
      document.body.appendChild(link);
      link.click();
    });

  };
  const customerRanking = (event) => {
    Axios.get('http://localhost:3001/reports/customer-ranking', {
      responseType: 'blob',
    }).then(response => {
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'customer_ranking.csv');
      document.body.appendChild(link);
      link.click();
    });

  };

  return (
    <div className="App">
      <header className="App-header">
        <form action="#">
          <div className="flex">
            <label htmlFor="customers">Customers</label>
            <input type="file" id="customers" accept=".csv"
              onChange={(event) => {
                setCustomers(event.target.files[0]);
              }}/>
          </div>
          <div className="flex">
            <label htmlFor="products">Products</label>
            <input
              type="file"
              id="products"
              accept=".csv"
              onChange={(event) => {
                setProducts(event.target.files[0]);
              }}
            />
          </div>
          <div className="flex">
            <label htmlFor="orders">Orders</label>
            <input type="file" id="orders" accept=".csv"
              onChange={(event) => {
                setOrders(event.target.files[0]);
              }}/>
          </div>
        </form>
        <button onClick={load}>Load</button>
        <button onClick={orderPrices}>Reporte 1</button>
        <button onClick={productCustomers}>Reporte 2</button>
        <button onClick={customerRanking}>Reporte 3</button>
      </header>
    </div>
  );
}

export default App;
